import React, { useState } from "react";

import exerciseData from "../data/writingData/data.json";

const WritingExercise = () => {
  const [exercise, setExercise] = useState("");
  const [sentence, setSentence] = useState("");
  const [choices, setChoices] = useState("");

  const startExercise = () => {
    setExercise(exerciseData[getRandomIndex()]);
    setSentence(exercise.sentence);
    setSentence(exercise.sentence);
  };

  const getRandomIndex = () => {
    return Math.floor(Math.random() * exerciseData.length);
  };
  return (
    <div className="WritingExercise__container">
      <div className="writingExercise">
        <div className="sentence">{exercise.sentence}</div>
        <div className="choices">{exercise.choices}</div>
      </div>

      <button onClick={startExercise}>Start Exercise</button>
    </div>
  );
};
export default WritingExercise;
