import React, { useState } from "react";

import exerciseData from "../data/writingData/data.json";

const WritingExercise = () => {
  const [exerciseState, setExerciseState] = useState({
    exerciseIndex: null,
    exercise: "",
  });

  const startExercise = () => {
    setExerciseState((prevState) => ({
      ...prevState,
      exerciseIndex: getRandomIndex(),
      exercise: exerciseData[exerciseIndex].sentence,
    }));
  };

  const getRandomIndex = () => {
    return Math.floor(Math.random() * exerciseData.length);
  };
  return (
    <div className="WritingExercise__container">
      <div className="sentence">{exerciseState.exercise}</div>
      <button onClick={startExercise}>Start Exercise</button>
    </div>
  );
};
export default WritingExercise;
