import React, { useState } from "react";

import exerciseData from "../data/writingData/data.json";

const WritingExercise = () => {
  const [exercise, setExercise] = useState({
    index: 0,
    sentence: "",
    choices: "",
  });
  const { sentence, choices } = exercise;

  const [selectedAnswer, setSelectedAnswer] = useState(null);
  const [submitted, setSubmitted] = useState(false);

  const startExercise = () => {
    const index = getRandomIndex();
    setExercise({
      index,
      sentence: exerciseData[index].sentence,
      choices: exerciseData[index].choices,
    });
  };

  const getRandomIndex = () => {
    return Math.floor(Math.random() * exerciseData.length);
  };

  const submitAnswers = (e) => {
    e.preventDefault();
    if (submitted) {
      return;
    }

    setSubmitted(true);
    // Do something with the selected answer, such as sending it to a server
    console.log(selectedAnswer);
  };

  const isSubmitDisabled = answers.includes("");

  return (
    <div className="WritingExercise__container">
      {sentence ? (
        <div className="writingExercise">
          <p>
            {sentence.split(" ").map((word, index) => (
              <span key={index}>
                <a
                  href={`https://translate.google.com/#en/ar/${word}`}
                  target="_blank"
                  rel="noreferrer"
                >
                  {word}
                </a>{" "}
              </span>
            ))}
          </p>
          <form onSubmit={submitAnswers}>
            {choices.map((choice) => (
              <div key={choice.number}>
                <p>{choice.number}. </p>
                {choice.options.map((option, optionIndex) => (
                  <label key={optionIndex}>
                    <input
                      type="radio"
                      value={option}
											onChange={(e) => handleAnswerChange(e, optionIndex)}
                    />
                    {option}
                  </label>
                ))}
              </div>
            ))}
            <button type="submit" disabled={isSubmitDisabled}>
              Submit
            </button>
          </form>

          <button onClick={startExercise}>Next Exercise</button>
        </div>
      ) : (
        <button onClick={startExercise}>Start Exercise</button>
      )}
    </div>
  );
};
export default WritingExercise;
