import React, { useState } from "react";

function QuizQuestion({ currentSentence, handleAnswerClick }) {
  const [selectedAnswers, setSelectedAnswers] = useState([]);

  const handleCheckboxChange = (isCorrect, isChecked) => {
    if (isChecked) {
      setSelectedAnswers([...selectedAnswers, isCorrect]);
    } else {
      setSelectedAnswers(selectedAnswers.filter(answer => answer !== isCorrect));
    }
  };

  return (
    <div>
      <p>{currentSentence.sentence}</p>
      {currentSentence.choices.map((choice, index) => (
        <label key={index}>
          <input
            type="checkbox"
            checked={selectedAnswers.includes(choice.isCorrect)}
            onChange={e => handleCheckboxChange(choice.isCorrect, e.target.checked)}
          />
          {choice.label}
        </label>
      ))}
      <button onClick={() => handleAnswerClick(selectedAnswers)}>Submit</button>
    </div>
  );
}

export default QuizQuestion;
