import React, { useState, useEffect } from "react";

const WritingExercise = () => {
  const [exerciseData, setExerciseData] = useState(null);
  const [sentenceIndex, setSentenceIndex] = useState(0);
  const [answers, setAnswers] = useState({});

  useEffect(() => {
    // Fetch the exercise data from the JSON file
    const fetchData = async () => {
      const response = await fetch("../data/writingData/data.json");
      const data = await response.json();
      
    };
    fetchData();
  }, []);

  const handleAnswerChange = (blankNumber, value) => {
    setAnswers({
      ...answers,
      [blankNumber]: value,
    });
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    // Check if all blanks are filled before submitting
    const allFilled = exerciseData.sentences[sentenceIndex].blanks.every(
      (blank) => answers[blank.number]
    );
    if (allFilled) {
      // Do something with the submitted answers
      console.log(answers);
      // Clear the answers for the next sentence
      setAnswers({});
      // Advance to the next sentence
      setSentenceIndex((prevIndex) =>
        prevIndex < exerciseData.sentences.length - 1 ? prevIndex + 1 : prevIndex
      );
    } else {
      alert("Please fill in all blanks before submitting.");
    }
  };

  if (!exerciseData) {
    return <p>Loading...</p>;
  }

  const sentence = exerciseData.sentences[sentenceIndex];
  const blankInputs = sentence.blanks.map((blank) => (
    <div key={blank.number}>
      <label htmlFor={`blank-${blank.number}`}>Blank {blank.number}:</label>
      <select
        id={`blank-${blank.number}`}
        value={answers[blank.number] || ""}
        onChange={(event) => handleAnswerChange(blank.number, event.target.value)}
      >
        <option value="">Select an option</option>
        {blank.options.map((option) => (
          <option key={option} value={option}>
            {option}
          </option>
        ))}
      </select>
    </div>
  ));

  return (
    <div>
      <h2>Fill in the Blanks</h2>
      <form onSubmit={handleSubmit}>
        <p>{sentence.text}</p>
        {blankInputs}
        <button type="submit">Submit</button>
      </form>
    </div>
  );
};

export default WritingExercise;
