import React, { useState } from "react";
import { Link } from "react-router-dom";

import "./Navbar.css";

function Navbar() {
  const [isNavClicked, setIsNavClicked] = useState(false);

  const handleNavClick = () => {
    setIsNavClicked(true);
  };

  return (
    <div className={isNavClicked ? "" : "home__container"}>
      {isNavClicked ? (
        <></>
      ) : (
        <h1 className="home__title">من العربية إلى الإنجليزية</h1>
      )}

      <div
        className={isNavClicked ? "nav-clicked" : "nav"}
        onClick={handleNavClick}
      >
        <nav>
          <ul>
            <li>
              <Link to="/vocabulary-exercise">
                Vocabulary Exercises
                <span className="text-bubble">
                  This is the Vocabulary Exercises section
                </span>
              </Link>
            </li>
            <li>
              <Link to="/reading-exercise">
                Reading Exercises
                <span className="text-bubble">
                  This is the Reading Exercises section
                </span>
              </Link>
            </li>
            <li>
              <Link to="/writing-exercise">
                Writing Exercise
                <span className="text-bubble">
                  This is the Writing Exercises section
                </span>
              </Link>
            </li>
          </ul>
        </nav>
      </div>
    </div>
  );
}

export default Navbar;
