import React, { useState } from "react";

import dater from "../data/readingData/data.json";
import beginner from "../data/readingData/beginner.json";
import intermediate from "../data/readingData/intermediate.json";
import advanced from "../data/readingData/advanced.json";

import JSONData from "../functions/exerciseLevelTracker";

function ReadingExercise() {
  const [text, setText] = useState("");
  const [questions, setQuestions] = useState([]);
  const [answers, setAnswers] = useState([]);
  const [score, setScore] = useState(0);
  const [submitted, setSubmitted] = useState(false);
  const [difficulty, setDifficulty] = useState("");

  const getLength = (data) => {
    const jsonDataObj = new JSONData(data);
    const rank = jsonDataObj.getTextsLengthRanking();
    // console.log(rank);
  };

  getLength(dater);

  const handleDifficultyChange = (difficulty) => {
    setDifficulty(difficulty);
  };

  const startExercise = () => {
    const data = {
      beginner,
      intermediate,
      advanced,
    };

    const texts = data[difficulty];
    const completedData =
      JSON.parse(sessionStorage.getItem("readingExerciseData")) || [];

    // filter out completed texts
    const availableData = texts.filter((text) => {
      return !completedData.some((data) => data.text === text.text);
    });

    if (availableData.length === 0) {
      // if all texts are completed, reset readingExerciseData array
      sessionStorage.removeItem("readingExerciseData");
      return;
    }

    const maxLength = score < 10 ? 100 : Infinity; // set the maximum length of displayed text
    const minLength = score >= 17 ? 100 : 0; // set the minimum length of displayed text

    const availableDataFiltered = availableData.filter((data) => {
      return data.text.length >= minLength && data.text.length <= maxLength;
    });

    if (availableDataFiltered.length === 0) {
      // if no texts meet the criteria, reset readingExerciseData array
      sessionStorage.removeItem("readingExerciseData");
      return;
    }

    const randomData =
      availableDataFiltered[
        Math.floor(Math.random() * availableDataFiltered.length)
      ];

    setText(randomData.text);
    setQuestions(randomData.questions);
    setAnswers(new Array(randomData.questions.length).fill(""));
    setSubmitted(false);
  };

  const handleAnswerChange = (e, index) => {
    const newAnswers = [...answers];
    newAnswers[index] = e.target.value;
    setAnswers(newAnswers);
  };

  const renderQuestion = (question, index) => (
    <div key={index}>
      <p>
        <span>
          <a
            href={`https://translate.google.com/#en/ar/${question.question}`}
            target="_blank"
            rel="noreferrer"
          >
            {question.question}
          </a>{" "}
        </span>
      </p>
      {question.options.map((option, optionIndex) => (
        <label
          key={optionIndex}
          className={
            submitted
              ? option === question.answer
                ? "correct"
                : "incorrect"
              : ""
          }
        >
          <input
            type="radio"
            name={`question-${index}`}
            value={option}
            checked={answers[index] === option}
            onChange={(e) => handleAnswerChange(e, index)}
          />
          {option}
        </label>
      ))}
    </div>
  );

  const handleSubmit = (e) => {
    e.preventDefault();

    if (submitted) {
      return;
    }

    const totalQuestions = questions.length;
    const tempScore = answers.reduce((acc, answer, index) => {
      return answer === questions[index].answer ? acc + 1 : acc;
    }, 0);
    console.log()
    const percentage = Math.round((tempScore / totalQuestions) * 100);

    setScore(tempScore);
    setSubmitted(true);

    // add the current text, answers, and score to the session storage
    const data =
      JSON.parse(sessionStorage.getItem("readingExerciseData")) || [];
    const newData = {
      text,
      questions,
      answers,
      percentage,
    };
    data.push(newData);
    sessionStorage.setItem("readingExerciseData", JSON.stringify(data));
  };

  const resetAnswers = () => {
    setAnswers(new Array(questions.length).fill(""));
    setSubmitted(false);
  };

  const isSubmitDisabled = answers.includes("");

  return (
    <div className="container">
      <h1>Reading Comprehension Exercise</h1>

      {text ? (
        <div>
          <p className="text">
            {text.split(" ").map((word, index) => (
              <span key={index}>
                <a
                  href={`https://translate.google.com/#en/ar/${word}`}
                  target="_blank"
                  rel="noreferrer"
                >
                  {word}
                </a>{" "}
              </span>
            ))}
          </p>
          <form onSubmit={handleSubmit}>
            {questions.map(renderQuestion)}
            <br />
            <button type="submit" disabled={isSubmitDisabled}>
              Submit
            </button>
            <p>Your score: {score} / 30</p>
          </form>
          <button
            className="next_exercise"
            onClick={() => {
              resetAnswers();
              startExercise();
            }}
          >
            Next Exercise
          </button>
        </div>
      ) : (
        <>
          <p>اقرأ النص ثم أجب على الأسئلة حول النص</p>
          <p>"Start Exercise" اختر صعوبة وانقر</p>
          <br></br>
          <div className="sidebar">
            <p>Select difficulty:</p>
            <ul>
              <li>
                <button
                  className={
                    difficulty === "beginner"
                      ? "difficulty__active"
                      : "difficulty"
                  }
                  onClick={() => handleDifficultyChange("beginner")}
                >
                  Beginner
                </button>
              </li>
              <li>
                <button
                  className={
                    difficulty === "intermediate"
                      ? "difficulty__active"
                      : "difficulty"
                  }
                  onClick={() => handleDifficultyChange("intermediate")}
                >
                  Intermediate
                </button>
              </li>
              <li>
                <button
                  className={
                    difficulty === "advanced"
                      ? "difficulty__active"
                      : "difficulty"
                  }
                  onClick={() => handleDifficultyChange("advanced")}
                >
                  Advanced
                </button>
              </li>
            </ul>
          </div>

          <button className="start_exercise" onClick={startExercise}>
            Start Exercise
          </button>
        </>
      )}
    </div>
  );
}

export default ReadingExercise;
