import React, { useState } from 'react';

const WritingExercise = () => {
  const [blank1, setBlank1] = useState('');
  const [blank2, setBlank2] = useState('');
  const [blank3, setBlank3] = useState('');
  const [blank4, setBlank4] = useState('');
  const [blank5, setBlank5] = useState('');
  
  const handleSubmit = (event) => {
    event.preventDefault();
    // Check if the answers are correct
    if (blank1 === 'market' && blank2 === 'store' && blank3 === 'surprised' && blank4 === 'market' && blank5 === 'impressed') {
      alert('Congratulations, you have completed the exercise!');
    } else {
      alert('Sorry, some of your answers are incorrect. Please try again!');
    }
  };
  
  return (
    <form onSubmit={handleSubmit}>
      <p>Tom was walking to the <input type="text" value={blank1} onChange={(event) => setBlank1(event.target.value)} /> to buy some groceries. On his way, he saw a <input type="text" value={blank2} onChange={(event) => setBlank2(event.target.value)} /> that was giving away free samples of ice cream. He decided to try some and was <input type="text" value={blank3} onChange={(event) => setBlank3(event.target.value)} /> by how delicious it was. When he got to the <input type="text" value={blank4} onChange={(event) => setBlank4(event.target.value)} />, he bought some ice cream to take home for later. He was so <input type="text" value={blank5} onChange={(event) => setBlank5(event.target.value)} /> by the ice cream that he decided to go back to the store and buy some more.</p>
      <button type="submit">Submit</button>
    </form>
  );
};

export default WritingExercise;
