import React, { useState } from "react";

import exerciseData from "../data/writingData/data.json";

const WritingExercise = () => {
  const [exercise, setExercise] = useState({
    index: 0,
    sentence: "",
    choices: "",
  });

  const { sentence, choices } = exercise;

  const startExercise = () => {
    const index = getRandomIndex();
    setExercise({
      index,
      sentence: exerciseData[index].sentence,
      choices: exerciseData[index].choices,
    });
  };

  const getRandomIndex = () => {
    return Math.floor(Math.random() * exerciseData.length);
  };
  return (
    <div className="WritingExercise__container">
      {sentence ? (
        <div className="writingExercise">
          <p>{sentence}</p>
          {choices.map((choice) => (
            <div key={choice.number}>
              <p>{choice.number}. </p>
              {choice.options.map((option, optionIndex) => (
                <label key={optionIndex}>
                  <input type="radio" value={option} />
                  {option}
                </label>
              ))}
            </div>
          ))}
          <button onClick={startExercise}>Next Exercise</button>
        </div>
      ) : (
        <button onClick={startExercise}>Start Exercise</button>
      )}
    </div>
  );
};
export default WritingExercise;
