import React from "react";
import PropTypes from "prop-types";
import Question from "./Question";
import Text from "./Text";
import UserStats from "./UserStats";

function Exercise({
  text,
  questions,
  answers,
  score,
  submitted,
  handleAnswerChange,
  handleSubmit,
  resetAnswers,
  isSubmitDisabled,
  handleStartNewExercise,
}) {
  return (
    <div>
      <Text text={text} />

      <form onSubmit={handleSubmit}>
        {questions.map((question, index) => (
          <Question
            key={index}
            question={question}
            index={index}
            answers={answers}
            submitted={submitted}
            handleAnswerChange={handleAnswerChange}
          />
        ))}
        <br />
        <button type="submit" disabled={isSubmitDisabled}>
          Submit
        </button>
      </form>
      <br></br>
      <UserStats score={score} />
      <br></br>

      <button onClick={resetAnswers}>Reset Answers</button>
      <button onClick={handleStartNewExercise}>Start New Exercise</button>
    </div>
  );
}

Exercise.propTypes = {
  text: PropTypes.string.isRequired,
  questions: PropTypes.array.isRequired,
  answers: PropTypes.array.isRequired,
  score: PropTypes.number.isRequired,
  submitted: PropTypes.bool.isRequired,
  handleAnswerChange: PropTypes.func.isRequired,
  handleSubmit: PropTypes.func.isRequired,
  resetAnswers: PropTypes.func.isRequired,
  isSubmitDisabled: PropTypes.bool.isRequired,
  handleStartNewExercise: PropTypes.func.isRequired,
};

export default Exercise;
