import React, { useState } from "react";

import sentences from "../data/writingData/data.json";

function FillInTheBlanks() {
  const [currentSentence, setCurrentSentence] = useState(getRandomSentence());

  function getRandomSentence() {
    const randomIndex = Math.floor(Math.random() * sentences.length);
    return sentences[randomIndex];
  }

  function handleAnswerClick(isCorrect) {
    if (isCorrect) {
      // handle correct answer
      setCurrentSentence(getRandomSentence());
    } else {
      // handle incorrect answer
    }
  }

  return (
    <div>
      <p>{currentSentence.sentence}</p>
      {currentSentence.choices.map((choice, index) => (
        <button key={index} onClick={() => handleAnswerClick(choice.isCorrect)}>
          {choice.label}
        </button>
      ))}
    </div>
  );
}

export default FillInTheBlanks;
