import React, { useState } from "react";
import {
  getRandomData,
  resetExerciseData,
  saveExerciseData,
} from "./ExerciseData";
import Introduction from "./Introduction";
import Exercise from "./Exercise";
import UserStats from "./UserStats";

function ReadingExercise() {
  const [exerciseData, setExerciseData] = useState(null);
  const [score, setScore] = useState(0);

  const startExercise = () => {
    const randomData = getRandomData(score);
  
    if (!randomData) {
      setText("");
      setQuestions([]);
      setAnswers([]);
      setSubmitted(false);
      return;
    }
  
    setText(randomData.text);
    setQuestions(randomData.questions);
    setAnswers(new Array(randomData.questions.length).fill(""));
    setSubmitted(false);
  };

  const handleReset = () => {
    resetExerciseData();
    setExerciseData(null);
    setScore(0);
  };

  const handleExerciseSubmit = (text, questions, answers, percentage) => {
    saveExerciseData(text, questions, answers, percentage);
    setScore((prevScore) => prevScore + percentage);
    setExerciseData(null);
  };

  const handleScoreReset = () => {
    resetExerciseData();
    setScore(0);
  };

  return (
    <div className="container">
      <h1>Reading Comprehension Exercise</h1>

      {exerciseData ? (
        <Exercise
          exerciseData={exerciseData}
          onSubmit={handleExerciseSubmit}
          onReset={handleReset}
        />
      ) : (
        <Introduction onStart={startExercise} />
      )}

      {score > 0 && (
        <div>
          <br />
          <UserStats score={score} onReset={handleScoreReset} />
        </div>
      )}
    </div>
  );
}

export default ReadingExercise;
