import React, { useState, useEffect } from "react";

import '../components/UserReadingStats.css'

const UserReadingStats = ({ score }) => {
  const [level, setLevel] = useState("unknown");

  useEffect(() => {
    if (score < 10) {
      setLevel("beginner");
    } else if (score > 10 && score < 17) {
      setLevel("intermediate");
    } else if (score > 17) {
      setLevel("advanced");
    }
  }, [score]);

  return <div className="Reader_level" id={level}>{level}</div>;
};
export default UserReadingStats;
