import React, { useState } from "react";
import exerciseData from "../data/writingData/data.json";

const WritingExercise = ({ exerciseData }) => {
    const [answers, setAnswers] = useState(
      exerciseData.sentences[0].blanks.reduce((acc, curr) => {
        acc[`blank${curr.number}`] = '';
        return acc;
      }, {})
    );
  
    const handleSubmit = (event) => {
      event.preventDefault();
      // Check if the answers are correct
      const correctAnswers = exerciseData.sentences[0].blanks.reduce((acc, curr) => {
        acc[`blank${curr.number}`] = curr.options[0];
        return acc;
      }, {});
      if (JSON.stringify(answers) === JSON.stringify(correctAnswers)) {
        alert('Congratulations, you have completed the exercise!');
      } else {
        alert('Sorry, some of your answers are incorrect. Please try again!');
      }
    };
  
    const handleChange = (event) => {
      setAnswers({
        ...answers,
        [event.target.name]: event.target.value
      });
    };
  
    return (
      <form onSubmit={handleSubmit}>
        <p>{exerciseData.sentences[0].text.split('_').map((word, index) => {
          if (index === exerciseData.sentences[0].text.split('_').length - 1) {
            return word;
          } else {
            const blankNumber = index + 1;
            const options = exerciseData.sentences[0].blanks.find(blank => blank.number === blankNumber).options;
            return (
              <span key={index}>
                <input type="text" name={`blank${blankNumber}`} value={answers[`blank${blankNumber}`]} onChange={handleChange} list={`options${blankNumber}`} />
                <datalist id={`options${blankNumber}`}>
                  {options.map((option, index) => <option key={index} value={option} />)}
                </datalist>
                {word}
              </span>
            );
          }
        })}</p>
        <button type="submit">Submit</button>
      </form>
    );
  };
  
  export default Exercise;
