import React, { useState } from "react";
import exerciseData from "../data/writingData/data.json";

const WritingExercise = () => {
  const [exercise, setExercise] = useState({
    index: 0,
    sentence: "",
    choices: [],
  });
  const [answers, setAnswers] = useState([]);
  const [submitted, setSubmitted] = useState(false);
  const [isCorrect, setIsCorrect] = useState(false);

  const getRandomIndex = () => Math.floor(Math.random() * exerciseData.length);

  const startExercise = () => {
    const index = getRandomIndex();
    const currentExercise = exerciseData[index];
    setExercise({
      index,
      sentence: currentExercise.sentence,
      choices: currentExercise.choices,
    });
    setAnswers([]);
    setSubmitted(false);
    setIsCorrect(false);
  };

  const handleAnswerChange = (e, index) => {
    const newAnswers = [...answers];
    newAnswers[index] = e.target.value;
    setAnswers(newAnswers);
  };

  const isSubmitDisabled = answers.includes("");

  const submitAnswers = (e) => {
    e.preventDefault();
    if (submitted) return;
    setSubmitted(true);
    const exerciseAnswers = exerciseData[exercise.index];
    if (!exerciseAnswers) {
      setIsCorrect(false);
      return;
    }

    const correctAnswers = exerciseAnswers.choices.map(
      (choice) => choice.answer
    );
    const isCorrect = correctAnswers.every(
      (answer, index) => answer === answers[index]
    );

    setIsCorrect(isCorrect);
  };

  return (
    <div className="WritingExercise__container">
      {exercise.sentence ? (
        <div className="writingExercise">
          <p>
            {exercise.sentence.split(" ").map((word, index) => (
              <span key={index}>
                <a
                  href={`https://translate.google.com/#en/ar/${word}`}
                  target="_blank"
                  rel="noreferrer"
                >
                  {word}
                </a>{" "}
              </span>
            ))}
          </p>
          <form onSubmit={submitAnswers}>
            {exercise.choices.map((choice, index) => (
              <div key={choice.number}>
                <p>{choice.number}. </p>
                {choice.options.map((option, optionIndex) => (
                  <label key={optionIndex}>
                    <input
                      type="radio"
                      value={option}
                      checked={answers[index] === option}
                      onChange={(e) => handleAnswerChange(e, index)}
                    />
                    {option}
                  </label>
                ))}
                {submitted && (
                  <div>
                    {choice.answer === answers[index] ? (
                      <span className="correct">{choice.answer}</span>
                    ) : (
                      <span className="incorrect">{answers[index]}</span>
                    )}
                  </div>
                )}
              </div>
            ))}
            <button type="submit" disabled={isSubmitDisabled}>
              Submit
            </button>
          </form>

          {submitted && (
            <div className="result">
              <button onClick={startExercise}>Next Exercise</button>
            </div>
          )}
        </div>
      ) : (
        <button onClick={startExercise}>Start Exercise</button>
      )}
    </div>
  );
};

export default WritingExercise;
